import smtplibimport getpassdef prompt(prompt):    return input(prompt).strip()fromaddr = prompt("From: ")toaddrs = prompt("To: ").split()print("Enter message, end with ^D (Unix) or ^Z (Windows):")# Add the From: and To: headers at the start!msg = ("From: %s\r\nTo: %s\r\n\r\n"       % (fromaddr, ", ".join(toaddrs)))while True:    try:        line = input()    except EOFError:        break    if not line:        break    msg = msg + lineprint("Message length is", len(msg))host = ['smtp.mail.yahoo.com',        'smtp.office365.com',        'smtp.gmail.com',        'smtp-mail-outlook.com',        'smtp.mail.me.com',        'smtp.aol.com',        ]def hostarr():    i = 0    hostlen = len(host)    while i < hostlen:        print(i, ': ', host[i])        i += 1print(hostarr())hostSelected = int(input("Enter the number corresponding to the host: "))host = host[hostSelected]print("Selected Host is:", host)portSelected = int(input("Enter PostNumber or Press Enter to set a default: ") or "587")port = portSelectedprint("Selected Port is:", port)password = getpass.getpass('Password: ')server = smtplib.SMTP(host, port)server.starttls()server.login(fromaddr, password)server.set_debuglevel(1)server.sendmail(fromaddr, toaddrs, msg)server.quit()print("---------Email sent to {}".format(toaddrs), " ---------->>>")